def version_kontrol():
    version_browser = webdriver.Chrome(executable_path=chromedriver, options=options_headless)
    str_version_control_chrome_browser = version_browser.capabilities['browserVersion']
    str_version_control_chromedriver = version_browser.capabilities['chrome']['chromedriverVersion'].split(' ')[0]

    if str_version_control_chrome_browser[0:2] != str_version_control_chromedriver[0:2]:
        version_browser.quit()
        return False
    else:
        version_browser.quit()
        return True

